                   // \u8fd4\u56de\u7eaf\u7ea2\u8272\n                   half4 color = SAMPLE_TEXTURE2D(_BlitTexture, sampler_LinearClamp, input.texcoord);\n                   half4 addValue = half4(0.25, 0.25, 0.25, 1);\n                   return color + addValue * _Intensity;\n               }\n               ENDHLSL\n           }\n       }\n   }\n'})})]}),"\n",(0,t.jsx)(n.h3,{id:"c\u4ee3\u7801\u5b9e\u73b0-renderfeature-\u548c-renderpass",children:"c#\u4ee3\u7801\u5b9e\u73b0 renderFeature \u548c RenderPass"}),"\n",(0,t.jsx)(n.p,{children:(0,t.jsx)(n.code,{children:"Unity6\u5b9e\u73b0"})}),"\n",(0,t.jsxs)(a,{children:[(0,t.jsx)("summary",{children:"RFAdjustColorRenderFeature.cs"}),(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{className:"language-csharp",children:'using UnityEngine;\nusing UnityEngine.Rendering;\nusing UnityEngine.Rendering.RenderGraphModule;\nusing UnityEngine.Rendering.RenderGraphModule.Util;\nusing UnityEngine.Rendering.Universal;\n\npublic class RFAdjustColorRenderFeature : ScriptableRendererFeature\n{\n    [SerializeField]\n    public float Intensity;\n\n    [SerializeField]\n    private Shader m_Shader;\n\n    private RFAdjustColorRenderPass m_RenderPass;\n    private Material m_Material;\n\n    public override void Create()\n    {\n        if (m_Shader == null)\n        {\n            Debug.LogError("Shader is not assigned");\n            return;\n        }\n\n        var data = new RFAdjustColorData();\n        data.Intensity = Intensity;\n\n        m_Material = new Material(m_Shader);\n        m_RenderPass = new RFAdjustColorRenderPass(m_Material, data);\n        m_RenderPass.renderPassEvent = RenderPassEvent.BeforeRenderingPostProcessing;\n    }\n\n    protected override void Dispose(bool disposing)\n    {\n        if (Application.isPlaying)\n        {\n            Destroy(m_Material);\n        }\n        else\n        {\n            DestroyImmediate(m_Material);\n        }\n    }\n\n    public override void AddRenderPasses(ScriptableRenderer renderer, ref RenderingData renderingData)\n    {\n        renderer.EnqueuePass(m_RenderPass);\n    }\n}\n\npublic class RFAdjustColorRenderPass : ScriptableRenderPass\n{\n    private Material m_Material;\n    private RenderTextureDescriptor m_RenderTextureDescriptor;\n    private RFAdjustColorData m_Data;\n\n    private static readonly int IntensityId = Shader.PropertyToID("_Intensity");\n\n    public RFAdjustColorRenderPass(Material material, RFAdjustColorData data)\n    {\n        m_Material = material;\n        m_Data = data;\n        m_RenderTextureDescriptor = new RenderTextureDescriptor(\n            Screen.width,\n            Screen.height,\n            RenderTextureFormat.Default,\n            0\n        );\n    }\n\n    public override void RecordRenderGraph(RenderGraph renderGraph, ContextContainer frameContext)\n    {\n        //read\n        UniversalResourceData resourceData = frameContext.Get<UniversalResourceData>();\n        UniversalCameraData cameraData = frameContext.Get<UniversalCameraData>();\n\n        TextureHandle srcCamColor = resourceData.activeColorTexture;\n\n        m_RenderTextureDescriptor.width = cameraData.cameraTargetDescriptor.width;\n        m_RenderTextureDescriptor.height = cameraData.cameraTargetDescriptor.height;\n        m_RenderTextureDescriptor.depthBufferBits = 0;\n\n        //\u521b\u5efa\u4e34\u65f6\u7eb9\u7406\n        TextureHandle dst = UniversalRenderer.CreateRenderGraphTexture(\n            renderGraph,\n            m_RenderTextureDescriptor,\n            "RFAdjustColorRenderPass",\n            false\n        );\n\n        m_Material.SetFloat(IntensityId, m_Data.Intensity);\n\n        //write\n        //\u5c06\u76f8\u673a\u989c\u8272\u4f20\u5165 \u540e\u5904\u7406shader\u8fdb\u884c\u5904\u7406\n        RenderGraphUtils.BlitMaterialParameters blitParams = new(srcCamColor, dst, m_Material, 0);\n        renderGraph.AddBlitPass(blitParams, "AdjustColor");\n\n        //\u5c06\u5904\u7406\u540e\u7684\u7ed3\u679c\u5199\u5165\u989c\u8272\u7f13\u51b2\u533a.\n        renderGraph.AddCopyPass(dst, resourceData.activeColorTexture);\n        \n    }\n}\n\npublic class RFAdjustColorData\n{\n    public float Intensity;\n}\n\nclass FinalCopyPassData\n{\n    public TextureHandle source;\n    public TextureHandle destination;\n}\n'})})]}),"\n",(0,t.jsx)(n.h3,{id:"\u521b\u5efa\u4e00\u4e2arenderdata",children:"\u521b\u5efa\u4e00\u4e2aRenderData"}),"\n",(0,t.jsxs)(n.p,{children:["\u5e76\u6dfb\u52a0\u5230renderAsset\u4e2d\u6dfb\u52a0\u8be5Render\n",(0,t.jsx)(n.img,{src:r(8299).A+"",width:"1389",height:"1084"}),"\n\u5728RenderData\u4e2d\u6dfb\u52a0RenderFeature\n",(0,t.jsx)(n.img,{src:r(9298).A+"",width:"1017",height:"1009"}),"\n\u5728\u4e3b\u6444\u50cf\u673a\u4e2d\u9009\u62e9\u8be5RenderData.\n",(0,t.jsx)(n.img,{src:r(861).A+"",width:"1133",height:"794"})]})]})}function u(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(c,{...e})}):c(e)}},4717:(e,n,r)=>{r.d(n,{A:()=>a});const a=r.p+"assets/images/renderFeature-cceabc82c3525262ecb2cee90d07358e.png"},8299:(e,n,r)=>{r.d(n,{A:()=>a});const a=r.p+"assets/images/renderFeature2-c1316ebcaa1508cbc06d55bd78e8059c.png"},9298:(e,n,r)=>{r.d(n,{A:()=>a});const a=r.p+"assets/images/renderFeature3-97c89e515e51a9c99ba2fea1f4fa0387.png"},861:(e,n,r)=>{r.d(n,{A:()=>a});const a=r.p+"assets/images/renderFeature4-72972726e8370865bb7f837d2f614c4f.png"},9391:(e,n,r)=>{r.d(n,{A:()=>a});const a=r.p+"assets/images/renderFeatureChangeColor-75c0f43c4ad3595982e06f2bd0ac9dbd.gif"},8453:(e,n,r)=>{r.d(n,{R:()=>i,x:()=>d});var a=r(6540);const t={},s=a.createContext(t);function i(e){const n=a.useContext(s);return a.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:i(e.components),a.createElement(s.Provider,{value:n},e.children)}}}]);